// üí† Criado por @prof.palomasilva - Automa√ß√£o Inteligente com n8n & IA üí†
// üí† Atualizado com CPA, Connect Rate, CTR e Hook Rate (calculado como video_continuous_2_sec_watched_actions/impressions somente para an√∫ncios que possuam essa m√©trica),
//     Outliers de CPA (positivos: CPA ‚â§ 60% da m√©dia; negativos: CPA ‚â• 140% da m√©dia),
//     classifica√ß√µes completas, sanitiza√ß√£o, datas din√¢micas, emojis e resumo r√°pido üí†

// Datas din√¢micas
const dataInicial = $input.item.json.data_inicial || "√∫ltimos 30 dias";
const dataFinal = $input.item.json.data_final || "";

// Verifica dados do Facebook
if (!items[0]) {
  throw new Error("Dados do Facebook n√£o recebidos.");
}

// Detecta estrutura do Facebook
let fbData;
if (Array.isArray(items[0].json)) {
  if (items[0].json.length > 0 && items[0].json[0].data) {
    fbData = items[0].json[0].data;
  } else {
    fbData = items[0].json;
  }
} else if (items[0].json.data) {
  fbData = items[0].json.data;
} else {
  fbData = [items[0].json];
}

// Fun√ß√£o para sanitizar nomes
const sanitizar = nome => nome.replace(/(Eduzz|Moni Cardoso|eduzz)/gi, "").slice(0, 40);

// Vari√°veis para m√©dias e totais
let totalSpend = 0;
let totalLeads = 0;
let sumFrequency = 0;
let sumCTR = 0;
let totalPurchaseValue = 0;
let totalPurchases = 0;
let totalConversas = 0;
let totalClicks = 0;
let totalViews = 0;
let totalHookHate = 0;
let countAds = fbData.length;
// Contador para an√∫ncios que possuem a m√©trica Hook Rate
let countAdsHookHate = 0;

let campanhasMap = {};

// Primeira passagem (agrega√ß√£o e campanhas)
fbData.forEach(ad => {
  const spend = parseFloat(ad.spend || 0);
  totalSpend += spend;

  const actions = ad.actions || [];
  const leadAction = actions.find(a => a.action_type === "lead")?.value || "0";
  const leads = parseInt(leadAction);
  totalLeads += leads;

  const frequency = parseFloat(ad.frequency || 0);
  sumFrequency += frequency;

  const ctr = parseFloat(ad.ctr || 0);
  sumCTR += ctr;

  const purchaseAction = actions.find(a => a.action_type === "purchase")?.value || "0";
  const purchases = parseInt(purchaseAction);
  totalPurchases += purchases;

  const actionValues = ad.action_values || [];
  const purchaseValue = parseFloat(actionValues.find(a => a.action_type === "purchase")?.value || "0");
  totalPurchaseValue += purchaseValue;

  totalConversas += parseInt(actions.find(a => a.action_type === "onsite_conversion.messaging_first_reply")?.value || "0");
  totalClicks += parseInt(actions.find(a => a.action_type === "link_click")?.value || "0");
  totalViews += parseInt(actions.find(a => a.action_type === "landing_page_view")?.value || "0");

  // Calcula Hook Rate (em percentual) somente se a m√©trica existir na forma de array
  let hookHateValue = 0;
  if (Array.isArray(ad.video_continuous_2_sec_watched_actions) && ad.video_continuous_2_sec_watched_actions.length > 0) {
    const videoWatched = parseFloat(ad.video_continuous_2_sec_watched_actions[0].value || 0);
    const impressions = parseFloat(ad.impressions || 0);
    hookHateValue = impressions > 0 ? (videoWatched / impressions) * 100 : 0;
    totalHookHate += hookHateValue;
    countAdsHookHate++;
  }

  // Agrupamento por campanha (incluindo Hook Rate se dispon√≠vel)
  if (!campanhasMap[ad.campaign_name]) {
    campanhasMap[ad.campaign_name] = { spend: 0, leads: 0, purchases: 0, purchaseValue: 0, hookHate: 0, count: 0 };
  }
  campanhasMap[ad.campaign_name].spend += spend;
  campanhasMap[ad.campaign_name].leads += leads;
  campanhasMap[ad.campaign_name].purchases += purchases;
  campanhasMap[ad.campaign_name].purchaseValue += purchaseValue;
  if (Array.isArray(ad.video_continuous_2_sec_watched_actions) && ad.video_continuous_2_sec_watched_actions.length > 0) {
    campanhasMap[ad.campaign_name].hookHate += hookHateValue;
  }
  campanhasMap[ad.campaign_name].count += 1;
});

// C√°lculo de m√©dias
const avgCPL = totalLeads > 0 ? totalSpend / totalLeads : 0;
const avgSpend = countAds > 0 ? totalSpend / countAds : 0;
const avgFrequency = countAds > 0 ? sumFrequency / countAds : 0;
const avgCTR = countAds > 0 ? sumCTR / countAds : 0;
const avgCPA = totalPurchases > 0 ? totalSpend / totalPurchases : 0;
const avgConnectRate = totalClicks > 0 ? (totalViews / totalClicks) * 100 : 0;
// A m√©dia de Hook Rate √© calculada em percentual
const avgHookHate = countAdsHookHate > 0 ? totalHookHate / countAdsHookHate : 0;

// Arrays para problemas e oportunidades
let problemasCPL = [], problemasGasto = [], problemasFrequenciaAlta = [];
let oportunidadesCPLBaixo = [], oportunidadesCTR = [], oportunidadesFrequenciaBaixa = [];
let problemasCPA = [], oportunidadesCPABaixo = [];
let problemasConnectRate = [], oportunidadesConnectRate = [];
let problemasCTR = [];
let problemasHookHate = [], oportunidadesHookHate = [];

// Arrays para outliers de CPA
let outliersPositivos = [];
let outliersNegativos = [];

// Arrays para classifica√ß√µes de an√∫ncios e p√∫blicos
let melhoresAnuncios = [], pioresAnuncios = [];
let melhoresPublicos = [], pioresPublicos = [];
let melhoresAnunciosCPC = [], pioresAnunciosCPC = [];
let melhoresPublicosCPC = [], pioresPublicosCPC = [];
let melhoresAnunciosCPA = [], pioresAnunciosCPA = [];
let melhoresPublicosCPA = [], pioresPublicosCPA = [];
let melhoresAnunciosHookHate = [], pioresAnunciosHookHate = [];
let melhoresPublicosHookHate = [], pioresPublicosHookHate = [];

// Arrays para campanhas
let campanhasArray = [], campanhasCPCArray = [], campanhasCPAArray = [];
let campanhasHookHateArray = [];

// Segunda passagem (an√°lise por an√∫ncio)
fbData.forEach(ad => {
  const spend = parseFloat(ad.spend || 0);
  const actions = ad.actions || [];

  const leadAction = actions.find(a => a.action_type === "lead")?.value || "0";
  const leads = parseInt(leadAction);
  const cpl = (spend > 0 && leads > 0) ? spend / leads : 0;

  const frequency = parseFloat(ad.frequency || 0);
  const ctr = parseFloat(ad.ctr || 0);
  const cpc = parseFloat(ad.cpc || 0);

  const purchaseAction = actions.find(a => a.action_type === "purchase")?.value || "0";
  const purchases = parseInt(purchaseAction);
  const cpa = (spend > 0 && purchases > 0) ? spend / purchases : 0;

  // Calcula Hook Rate para o an√∫ncio (em percentual)
  let hookHate = null;
  if (Array.isArray(ad.video_continuous_2_sec_watched_actions) && ad.video_continuous_2_sec_watched_actions.length > 0) {
    const videoWatched = parseFloat(ad.video_continuous_2_sec_watched_actions[0].value || 0);
    const impressions = parseFloat(ad.impressions || 0);
    hookHate = impressions > 0 ? (videoWatched / impressions) * 100 : 0;
  }

  // Outliers de CPA: an√∫ncios com CPA <= 60% da m√©dia (positivos) ou >= 140% da m√©dia (negativos)
  if (purchases > 0 && cpa > 0) {
    if (cpa <= avgCPA * 0.6) {
      outliersPositivos.push(`(CPA: ${cpa.toFixed(2)} vs m√©dia ${avgCPA.toFixed(2)}) ${sanitizar(ad.ad_name)} (${ad.ad_id})`);
    }
    if (cpa >= avgCPA * 1.4) {
      outliersNegativos.push(`(CPA: ${cpa.toFixed(2)} vs m√©dia ${avgCPA.toFixed(2)}) ${sanitizar(ad.ad_name)} (${ad.ad_id})`);
    }
  }

  // Problemas & oportunidades
  if (cpl > avgCPL && spend >= avgSpend) {
    problemasCPL.push(`(R$ ${cpl.toFixed(2)} vs m√©dia R$ ${avgCPL.toFixed(2)}) ${sanitizar(ad.ad_name)} (${ad.ad_id})`);
  }
  if (cpl > 0 && cpl < avgCPL) {
    oportunidadesCPLBaixo.push(`(R$ ${cpl.toFixed(2)} vs m√©dia R$ ${avgCPL.toFixed(2)}) ${sanitizar(ad.ad_name)} (${ad.ad_id})`);
  }
  if (spend > avgSpend && leads === 0) {
    problemasGasto.push(`(R$ ${spend.toFixed(2)} vs m√©dia R$ ${avgSpend.toFixed(2)}) ${sanitizar(ad.campaign_name)}`);
  }
  if (frequency > avgFrequency) {
    problemasFrequenciaAlta.push(`(Frequ√™ncia: ${frequency.toFixed(2)} vs m√©dia ${avgFrequency.toFixed(2)}) ${sanitizar(ad.ad_name)} (${ad.ad_id})`);
  }
  // CTR: oportunidades e problemas
  if (ctr > avgCTR) {
    oportunidadesCTR.push(`(CTR: ${ctr.toFixed(2)}% vs m√©dia ${avgCTR.toFixed(2)}%) ${sanitizar(ad.ad_name)} (${ad.ad_id})`);
  }
  if (ctr < avgCTR) {
    problemasCTR.push(`(CTR: ${ctr.toFixed(2)}% vs m√©dia ${avgCTR.toFixed(2)}%) ${sanitizar(ad.ad_name)} (${ad.ad_id})`);
  }
  if (ctr > avgCTR && frequency < avgFrequency) {
    oportunidadesFrequenciaBaixa.push(`(CTR: ${ctr.toFixed(2)}%, Frequ√™ncia: ${frequency.toFixed(2)} vs m√©dia ${avgFrequency.toFixed(2)}) ${sanitizar(ad.ad_name)} (${ad.ad_id})`);
  }
  // CPA: oportunidades e problemas
  if (purchases > 0) {
    if (cpa > avgCPA && spend >= avgSpend) {
      problemasCPA.push(`(R$ ${cpa.toFixed(2)} vs m√©dia R$ ${avgCPA.toFixed(2)}) ${sanitizar(ad.ad_name)} (${ad.ad_id})`);
    }
    if (cpa > 0 && cpa < avgCPA) {
      oportunidadesCPABaixo.push(`(R$ ${cpa.toFixed(2)} vs m√©dia R$ ${avgCPA.toFixed(2)}) ${sanitizar(ad.ad_name)} (${ad.ad_id})`);
    }
  }
  // Connect Rate: oportunidades e problemas
  const linkClicks = parseInt(actions.find(a => a.action_type === "link_click")?.value || "0");
  const landingPageViews = parseInt(actions.find(a => a.action_type === "landing_page_view")?.value || "0");
  const connectRate = linkClicks > 0 ? (landingPageViews / linkClicks) * 100 : 0;
  if (linkClicks > 0) {
    if (connectRate < avgConnectRate) {
      problemasConnectRate.push(`(Connect Rate: ${connectRate.toFixed(2)}% vs m√©dia ${avgConnectRate.toFixed(2)}%) ${sanitizar(ad.ad_name)} (${ad.ad_id})`);
    } else {
      oportunidadesConnectRate.push(`(Connect Rate: ${connectRate.toFixed(2)}% vs m√©dia ${avgConnectRate.toFixed(2)}%) ${sanitizar(ad.ad_name)} (${ad.ad_id})`);
    }
  }
  // Hook Rate: classifica somente se a m√©trica existir e ignorando valores 0 (valor em percentual)
  if (hookHate !== null && hookHate !== 0) {
    if (hookHate > avgHookHate) {
      oportunidadesHookHate.push(`(Hook Rate: ${hookHate.toFixed(2)}% vs m√©dia ${avgHookHate.toFixed(2)}%) ${sanitizar(ad.ad_name)} (${ad.ad_id})`);
    } else if (hookHate < avgHookHate) {
      problemasHookHate.push(`(Hook Rate: ${hookHate.toFixed(2)}% vs m√©dia ${avgHookHate.toFixed(2)}%) ${sanitizar(ad.ad_name)} (${ad.ad_id})`);
    }
  }

  // Classifica√ß√µes por an√∫ncio e p√∫blico
  if (cpl > 0) {
    melhoresAnuncios.push({ nome: sanitizar(ad.ad_name), cpl, ad_id: ad.ad_id });
    melhoresPublicos.push({ nome: sanitizar(ad.adset_name), cpl, adset_id: ad.adset_id });
  }
  if (cpc > 0) {
    melhoresAnunciosCPC.push({ nome: sanitizar(ad.ad_name), cpc, ad_id: ad.ad_id });
    melhoresPublicosCPC.push({ nome: sanitizar(ad.adset_name), cpc, adset_id: ad.adset_id });
  }
  if (cpa > 0) {
    melhoresAnunciosCPA.push({ nome: sanitizar(ad.ad_name), cpa, ad_id: ad.ad_id });
    melhoresPublicosCPA.push({ nome: sanitizar(ad.adset_name), cpa, adset_id: ad.adset_id });
  }
  // Classifica√ß√£o por Hook Rate: apenas para an√∫ncios que possuem a m√©trica (valor em percentual)
  if (hookHate !== null && hookHate !== 0) {
    melhoresAnunciosHookHate.push({ nome: sanitizar(ad.ad_name), hookHate: hookHate.toFixed(2), ad_id: ad.ad_id });
    melhoresPublicosHookHate.push({ nome: sanitizar(ad.adset_name), hookHate: hookHate.toFixed(2), adset_id: ad.adset_id });
  }
});

// Ordena√ß√µes de an√∫ncios e p√∫blicos
melhoresAnuncios.sort((a, b) => a.cpl - b.cpl);
pioresAnuncios = [...melhoresAnuncios].reverse();
melhoresPublicos.sort((a, b) => a.cpl - b.cpl);
pioresPublicos = [...melhoresPublicos].reverse();

melhoresAnunciosCPC.sort((a, b) => a.cpc - b.cpc);
pioresAnunciosCPC = [...melhoresAnunciosCPC].reverse();
melhoresPublicosCPC.sort((a, b) => a.cpc - b.cpc);
pioresPublicosCPC = [...melhoresPublicosCPC].reverse();

melhoresAnunciosCPA.sort((a, b) => a.cpa - b.cpa);
pioresAnunciosCPA = [...melhoresAnunciosCPA].reverse();
melhoresPublicosCPA.sort((a, b) => a.cpa - b.cpa);
pioresPublicosCPA = [...melhoresPublicosCPA].reverse();

melhoresAnunciosHookHate.sort((a, b) => parseFloat(a.hookHate) - parseFloat(b.hookHate));
pioresAnunciosHookHate = [...melhoresAnunciosHookHate].reverse();
melhoresPublicosHookHate.sort((a, b) => parseFloat(a.hookHate) - parseFloat(b.hookHate));
pioresPublicosHookHate = [...melhoresPublicosHookHate].reverse();

// Classifica√ß√£o por campanhas para CPL, CPC e CPA
for (const [nome, data] of Object.entries(campanhasMap)) {
  const cplCampanha = (data.spend > 0 && data.leads > 0) ? data.spend / data.leads : 0;
  const cpcCampanha = (data.spend > 0 && data.leads > 0) ? data.spend / data.leads : 0;
  const cpaCampanha = (data.spend > 0 && data.purchases > 0) ? data.spend / data.purchases : 0;

  if (cplCampanha > 0) campanhasArray.push({ nome: sanitizar(nome), cpl: cplCampanha, campaign_name: nome });
  if (cpcCampanha > 0) campanhasCPCArray.push({ nome: sanitizar(nome), cpc: cpcCampanha, campaign_name: nome });
  if (cpaCampanha > 0) campanhasCPAArray.push({ nome: sanitizar(nome), cpa: cpaCampanha, campaign_name: nome });
}

// Classifica√ß√£o por campanhas para Hook Rate (m√©dia por campanha, considerando apenas an√∫ncios que possuem a m√©trica)
for (const [nome, data] of Object.entries(campanhasMap)) {
  const hookHateCampanha = data.hookHate > 0 ? data.hookHate / data.count : 0;
  campanhasHookHateArray.push({ nome: sanitizar(nome), hookHate: hookHateCampanha.toFixed(2), campaign_name: nome });
}

campanhasArray.sort((a, b) => a.cpl - b.cpl);
campanhasCPCArray.sort((a, b) => a.cpc - b.cpc);
campanhasCPAArray.sort((a, b) => a.cpa - b.cpa);
campanhasHookHateArray.sort((a, b) => parseFloat(a.hookHate) - parseFloat(b.hookHate));

const melhorCampanha = campanhasArray[0] || "Nenhuma campanha";
const piorCampanha = campanhasArray[campanhasArray.length - 1] || "Nenhuma campanha";
const melhorCampanhaCPC = campanhasCPCArray[0] || "Nenhuma campanha";
const piorCampanhaCPC = campanhasCPCArray[campanhasCPCArray.length - 1] || "Nenhuma campanha";
const melhorCampanhaCPA = campanhasCPAArray[0] || "Nenhuma campanha";
const piorCampanhaCPA = campanhasCPAArray[campanhasCPAArray.length - 1] || "Nenhuma campanha";
const melhorCampanhaHookHate = campanhasHookHateArray[0] || "Nenhuma campanha";
const piorCampanhaHookHate = campanhasHookHateArray[campanhasHookHateArray.length - 1] || "Nenhuma campanha";

// Resumo r√°pido
const resumo = `üöÄ *Resumo*: ${oportunidadesCPLBaixo.length} an√∫ncios com CPL baixo, ${problemasCPL.length} com CPL alto, ${problemasFrequenciaAlta.length} com frequ√™ncia alta.`;

// Output (Hook Rate exibido como percentual)
return [{
  json: {
    // Per√≠odo din√¢mico, usando o valor do node "Dados"
    periodo: $node["Data"].json["Date Preset"],
    totais: {
      "Valor Investido": totalSpend.toLocaleString('pt-BR', { minimumFractionDigits: 2 }),
      "Leads": totalLeads,
      "Compras": totalPurchases,
      "Valor Compras": totalPurchaseValue.toLocaleString('pt-BR', { minimumFractionDigits: 2 }),
      "CPA M√©dio": avgCPA.toFixed(2),
      "Conversas Iniciadas": totalConversas,
      "Cliques no link": totalClicks,
      "Visitas √† p√°gina": totalViews,
      "Connect Rate M√©dio (%)": avgConnectRate.toFixed(2),
      "Hook Rate M√©dio": avgHookHate.toFixed(2) + "%"  // Exibido como percentual
    },
    problemas: {
      "CPL Alto": problemasCPL,
      "Frequ√™ncia Alta": problemasFrequenciaAlta,
      "Gasto sem Leads": problemasGasto,
      "CTR Baixo": problemasCTR,
      "CPA Alto": problemasCPA,
      "Connect Rate Baixo": problemasConnectRate,
      "Hook Rate Baixo": problemasHookHate
    },
    oportunidades: {
      "CPL Baixo": oportunidadesCPLBaixo,
      "CTR Excelente": oportunidadesCTR,
      "Baixa Frequ√™ncia c/ Bom CTR": oportunidadesFrequenciaBaixa,
      "CPA Baixo": oportunidadesCPABaixo,
      "Connect Rate Excelente": oportunidadesConnectRate,
      "Hook Rate Alto": oportunidadesHookHate
    },
    outliers: {
      "Positivos": outliersPositivos,
      "Negativos": outliersNegativos
    },
    classificacoes: {
      // CPL
      "üèÜ Melhor An√∫ncio CPL": melhoresAnuncios[0] || "Nenhum an√∫ncio",
      "üî• Pior An√∫ncio CPL": pioresAnuncios[0] || "Nenhum an√∫ncio",
      "üí† Melhor P√∫blico CPL": melhoresPublicos[0] || "Nenhum p√∫blico",
      "‚ö†Ô∏è Pior P√∫blico CPL": pioresPublicos[0] || "Nenhum p√∫blico",
      "üìà Melhor Campanha CPL": melhorCampanha,
      "üìâ Pior Campanha CPL": piorCampanha,
      // CPC
      "üèÜ Melhor An√∫ncio CPC": melhoresAnunciosCPC[0] || "Nenhum an√∫ncio",
      "üî• Pior An√∫ncio CPC": pioresAnunciosCPC[0] || "Nenhum an√∫ncio",
      "üí† Melhor P√∫blico CPC": melhoresPublicosCPC[0] || "Nenhum p√∫blico",
      "‚ö†Ô∏è Pior P√∫blico CPC": pioresPublicosCPC[0] || "Nenhum p√∫blico",
      "üìà Melhor Campanha CPC": melhorCampanhaCPC,
      "üìâ Pior Campanha CPC": piorCampanhaCPC,
      // CPA
      "üèÜ Melhor An√∫ncio CPA": melhoresAnunciosCPA[0] || "Nenhum an√∫ncio",
      "üî• Pior An√∫ncio CPA": pioresAnunciosCPA[0] || "Nenhum an√∫ncio",
      "üí† Melhor P√∫blico CPA": melhoresPublicosCPA[0] || "Nenhum p√∫blico",
      "‚ö†Ô∏è Pior P√∫blico CPA": pioresPublicosCPA[0] || "Nenhum p√∫blico",
      "üìà Melhor Campanha CPA": melhorCampanhaCPA,
