const MAX_ITEMS = 10;

const datePresetMap = {
  "today": "hoje",
  "yesterday": "ontem",
  "last_3d": "dos √∫ltimos 3 dias",
  "last_7d": "dos √∫ltimos 7 dias",
  "last_14d": "dos √∫ltimos 14 dias",
  "last_30d": "dos √∫ltimos 30 dias",
  "last_90d": "dos √∫ltimos 90 dias",
  "this_month": "deste m√™s",
  "last_month": "do m√™s passado",
  "this_quarter": "deste trimestre",
  "lifetime": "do per√≠odo total"
};

const presetKey = $node["Data"].json["Date Preset"] || "last_14d";
const datePresetText = datePresetMap[presetKey] || "dos √∫ltimos 14 dias";

function getPeriodDates(presetKey) {
  let today = new Date();
  let startDate = new Date();
  switch (presetKey) {
    case "today": break;
    case "yesterday": startDate.setDate(today.getDate() - 1); break;
    case "last_3d": startDate.setDate(today.getDate() - 3); break;
    case "last_7d": startDate.setDate(today.getDate() - 7); break;
    case "last_14d": startDate.setDate(today.getDate() - 14); break;
    case "last_30d": startDate.setDate(today.getDate() - 30); break;
    case "last_90d": startDate.setDate(today.getDate() - 90); break;
    case "this_month": startDate = new Date(today.getFullYear(), today.getMonth(), 1); break;
    case "last_month":
      startDate = new Date(today.getFullYear(), today.getMonth() - 1, 1);
      today = new Date(today.getFullYear(), today.getMonth(), 0);
      break;
    case "this_quarter":
